mapper.select:
select c.coach_id, c.user_id, c.start_time, c.end_time, t.dict_item_name coach_type, s.dict_item_name coach_style, c.coach_status, c.full_class, c.expected_salary, a.dict_item_name authentication, c.deal_account, c.not_completed, c.good_comment, c.bad_comment, c.common_comment from coach c left join base_dict t on c.coach_type = t.dict_id left join base_dict s on c.coach_style = s.dict_id left join base_dict a on c.authentication = a.dict_id
and c.coach_type like concat('%',#{coach.coachType},'%')
and c.coach_style like concat('%',#{coach.coachStyle},'%')
and c.expected_salary between #{downExpectedSalary} and #{upExpectedSalary}
and c.expected_salary < #{upExpectedSalary}
and c.expected_salary < #{upExpectedSalary}

select t.dict_item_name coach_type, s.dict_item_name coach_style, u.real_name, u.user_phone, u.user_qq, k.real_name venueName, c.deal_account, c.not_completed, c.coach_detail, c.good_comment, c.bad_comment, c.common_comment from coach c left join `user` u on c.user_id = u.user_id left join base_dict t on c.coach_type = t.dict_id left join base_dict s on c.coach_style = s.dict_id left join venue v on c.venue_id = v.venue_id left join `user` k on v.user_id = k.user_id
and real_name like concat('%',#{coachInformationVO.realName},'%')
and coach_type like concat('%',#{coachInformationVO.coachType},'%')
and coach_style like concat('%',#{coachInformationVO.coachStyle},'%')
and c.coach_id = #{coachInformationVO.coachId}
and c.venue_id = #{coachInformationVO.venueId}
